        />

        {/* Hamburger Menu Button */}
        <button
          onClick={() => setMenuOpen(!menuOpen)}
          className="absolute top-4 right-4 z-[1000] bg-purple-600 hover:bg-purple-700 text-white p-3 rounded-full shadow-lg transition-all transform hover:scale-110"
          aria-label="Menu"
        >
          <svg
            className="w-6 h-6"
